// Parse RSS XML content - improved version
const items = [];

// Get the XML data from the HTTP request
let xmlData = $input.first().json?.data || $input.first().binary?.data || $input.first().json;

// Handle different data formats
if (typeof xmlData !== 'string') {
  if (xmlData && typeof xmlData === 'object') {
    xmlData = JSON.stringify(xmlData);
  } else {
    xmlData = String(xmlData || '');
  }
}

console.log('XML data type:', typeof xmlData);
console.log('XML data length:', xmlData.length);
console.log('First 200 chars:', xmlData.substring(0, 200));

if (xmlData && xmlData.length > 0) {
  try {
    // Simple XML parsing for RSS feed items
    const itemMatches = xmlData.match(/<item[^>]*>([\s\S]*?)<\/item>/g);

    if (itemMatches && itemMatches.length > 0) {
      console.log('Found', itemMatches.length, 'RSS items');

      for (let i = 0; i < Math.min(itemMatches.length, 5); i++) {
        const item = itemMatches[i];

        // Extract title with better regex
        const titleMatch = item.match(/<title>(?:<!\[CDATA\[)?([^\]<]+)(?:\]\]>)?<\/title>/i);
        const title = titleMatch ? titleMatch[1].trim() : `n8n Blog Post ${i + 1}`;

        // Extract link
        const linkMatch = item.match(/<link>([^<]+)<\/link>/i);
        const link = linkMatch ? linkMatch[1].trim() : 'https://blog.n8n.io';

        // Extract description with better handling
        const descMatch = item.match(/<description>(?:<!\[CDATA\[)?([^\]]+?)(?:\]\]>)?<\/description>/i);
        let description = descMatch ? descMatch[1].trim() : 'Latest n8n updates and features';

        // Clean HTML tags and limit length
        description = description.replace(/<[^>]+>/g, '').substring(0, 200);
        if (description.length === 200) description += '...';

        // Extract publication date
        const pubDateMatch = item.match(/<pubDate>([^<]+)<\/pubDate>/i);
        const publishedDate = pubDateMatch ? pubDateMatch[1].trim() : new Date().toISOString();

        items.push({
          title: title,
          link: link,
          description: description,
          publishedDate: publishedDate,
          type: 'blog_post',
          source: 'n8n_blog'
        });

        console.log(`Parsed item ${i + 1}: ${title}`);
      }
    } else {
      console.log('No RSS items found in XML');
    }
  } catch (error) {
    console.error('Error parsing RSS XML:', error.message);
  }
}

// Always return at least one item (fallback)
if (items.length === 0) {
  console.log('Creating fallback item');
  items.push({
    title: 'Latest n8n Updates',
    link: 'https://blog.n8n.io',
    description: 'Check out the latest features and updates from the n8n team',
    publishedDate: new Date().toISOString(),
    type: 'blog_post',
    source: 'n8n_blog'
  });
}

console.log('Total items to return:', items.length);
return items.map(item => ({ json: item }));

// Parse GitHub releases
const releases = $input.first().json;
const items = [];

if (Array.isArray(releases)) {
  const recentReleases = releases.slice(0, 3);

  for (const release of recentReleases) {
    items.push({
      title: `n8n ${release.tag_name} Released`,
      link: release.html_url || 'https://github.com/n8n-io/n8n/releases',
      description: (release.body || 'New version released with exciting features and improvements').substring(0, 200),
      publishedDate: release.published_at || new Date().toISOString(),
      type: 'release',
      version: release.tag_name || 'latest',
      isPrerelease: release.prerelease || false,
      source: 'github_releases'
    });
  }
}

if (items.length === 0) {
  items.push({
    title: 'n8n Latest Release',
    link: 'https://github.com/n8n-io/n8n/releases',
    description: 'Check out the latest n8n release with new features and improvements',
    publishedDate: new Date().toISOString(),
    type: 'release',
    source: 'github_releases'
  });
}

return items.map(item => ({ json: item }));

// Filter recent content or get latest items
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

const recentContent = [];
const allItems = $input.all();

for (const item of allItems) {
  const publishedDate = new Date(item.json.publishedDate);
  if (publishedDate >= sevenDaysAgo) {
    recentContent.push(item);
  }
}

if (recentContent.length === 0) {
  allItems.sort((a, b) => new Date(b.json.publishedDate) - new Date(a.json.publishedDate));
  return allItems.slice(0, 3);
}

return recentContent;

// Prepare content for AI
const allContent = $input.all();
const contentSummary = allContent.map(item => ({
  title: item.json.title,
  description: item.json.description,
  type: item.json.type,
  source: item.json.source,
  link: item.json.link
}));

return [{ json: { content: contentSummary } }];

// Format AI response into structured posts
const aiResponse = $input.first().json.choices?.[0]?.text || $input.first().json.text || '';
const postSections = aiResponse.split(/POST \d+:/i).filter(section => section.trim().length > 0);
const formattedPosts = [];

for (let i = 0; i < Math.min(postSections.length, 5); i++) {
  const section = postSections[i].trim();

  const titleMatch = section.match(/Title:\s*([^\n]+)/i);
  const contentMatch = section.match(/Content:\s*([\s\S]*?)(?=Hashtags:|$)/i);
  const hashtagsMatch = section.match(/Hashtags:\s*([^\n]+)/i);
  const ctaMatch = section.match(/CTA:\s*([^\n]+)/i);

  const post = {
    id: `post_idea_${i + 1}`,
    title: titleMatch ? titleMatch[1].trim() : `n8n Feature Post ${i + 1}`,
    content: contentMatch ? contentMatch[1].trim() : 'Exciting n8n updates are here!',
    hashtags: hashtagsMatch ? hashtagsMatch[1].trim() : '#n8n #automation #nocode #workflow',
    callToAction: ctaMatch ? ctaMatch[1].trim() : 'Try n8n today!',
    generatedAt: new Date().toISOString(),
    status: 'draft'
  };

  formattedPosts.push(post);
}

if (formattedPosts.length === 0) {
  formattedPosts.push({
    id: 'post_idea_1',
    title: 'ðŸš€ Discover n8n Automation',
    content: 'Ready to supercharge your workflows? ðŸ”¥ n8n connects your favorite tools without code! With 400+ integrations and visual workflows, automate anything. Join thousands transforming their productivity! âœ¨',
    hashtags: '#n8n #automation #nocode #workflow #productivity',
    callToAction: 'Start automating today - try n8n for free!',
    generatedAt: new Date().toISOString(),
    status: 'draft'
  });
}

return formattedPosts.map(post => ({ json: post }));

// Create summary
const posts = $input.all();
const summary = {
  totalPosts: posts.length,
  generatedAt: new Date().toISOString(),
  posts: posts.map(post => ({
    id: post.json.id,
    title: post.json.title
  })),
  message: `ðŸš€ ${posts.length} new LinkedIn post ideas generated!\n\nPosts:\n${posts.map((post, i) => `${i+1}. ${post.json.title}`).join('\n')}\n\nReady for review!`
};

return [{ json: summary }];
