// This script cleans the assessment data and wraps it in a single 'data' field 
// for efficient token consumption by the AI Agent.

const inputItem = $input.first().json;

// --- Helper function to safely extract the response value ---
function getResponseValue(response) {
    if (response === null) return null;

    // Handle specific boolean response
    if (typeof response.response === 'boolean') {
        return response.response;
    }
    // Handle array response (multiselect)
    if (Array.isArray(response.response)) {
        return response.response;
    }
    // Return number/string response, or null if key is missing
    if (response.response !== undefined) {
        return response.response;
    }
    return null;
}

// --- 1. Distill Questions and Answers ---
const cleanedTests = inputItem.test_results.map(test => {
    // Distill questions and answers
    const cleanedQuestions = test.questions.map(question => ({
        question_text: question.question_text,
        question_type: question.question_type,
        response: getResponseValue(question.response),
        unit: question.validation_rules?.unit || null 
    }));

    return {
        test_name: test.test_name,
        test_category: test.test_category,
        completed_at: test.completed_at,
        questions_and_answers: cleanedQuestions
    };
});

// --- 2. Build the Final Optimized Payload ---
const optimizedPayload = {
    // === FIX: Retrieve session_id and form_type directly from the input item ===
    session_id: inputItem.sessionId || null, 
    form_type: inputItem.form_type || null, 
    // =========================================================================

    // Essential Student/Coach Context
    student_id: inputItem.assessment.student_id,
    assessment_id: inputItem.assessment.id,

    student_profile: {
        full_name: inputItem.assessment.student.full_name,
        date_of_birth: inputItem.assessment.student.date_of_birth,
        fitness_goal: inputItem.assessment.student.fitness_goal,
    },

    coach_info: {
        full_name: inputItem.assessment.coach.full_name,
        specialties: inputItem.assessment.coach.specialties
    },

    // Distilled Test Results
    tests: cleanedTests,

    // Key Summary Findings (most valuable for LLM analysis)
    summary_findings: {
        bilateral_comparisons: inputItem.summary.bilateral_comparisons,
        key_findings: inputItem.summary.key_findings,
        recommendations: inputItem.summary.recommendations
    }
};

// --- 3. Wrap in the required 'data' field and return ---
return [{ 
    json: {
        data: optimizedPayload 
    }
}];

// ============================================================================
// PARSE LONG PROFILE - ENHANCED VERSION WITH AGGRESSIVE CLEANING
// ============================================================================

const input = $input.first().json;
console.log('=== Parse Long Profile - START ===');

// --- HELPER FUNCTION: Extract JSON from any format ---
function extractJSON(data) {
  let jsonString;

  // If already an object, stringify it
  if (typeof data === 'object' && data !== null) {
    return data;
  }

  // If string, clean it aggressively
  if (typeof data === 'string') {
    jsonString = data;

    // Remove all markdown code block markers
    jsonString = jsonString.replace(/```(?:json|javascript|js)?\s*/gi, '');
    jsonString = jsonString.replace(/```\s*/g, '');

    // Remove leading "json" or "JSON" text
    jsonString = jsonString.replace(/^json\s*/i, '');

    // Trim whitespace
    jsonString = jsonString.trim();

    // Find the first { and last } using brace matching
    const firstBrace = jsonString.indexOf('{');
    if (firstBrace === -1) {
      throw new Error('No opening brace found in agent output');
    }

    // Match braces to find the complete JSON object
    let braceCount = 0;
    let lastBrace = -1;

    for (let i = firstBrace; i < jsonString.length; i++) {
      if (jsonString[i] === '{') braceCount++;
      if (jsonString[i] === '}') {
        braceCount--;
        if (braceCount === 0) {
          lastBrace = i;
          break;
        }
      }
    }

    if (lastBrace === -1) {
      throw new Error('No matching closing brace found');
    }

    jsonString = jsonString.substring(firstBrace, lastBrace + 1);

    // Parse the cleaned JSON
    try {
      return JSON.parse(jsonString);
    } catch (e) {
      console.error('JSON Parse Error:', e.message);
      console.error('Cleaned JSON (first 500 chars):', jsonString.substring(0, 500));
      throw new Error(`Failed to parse JSON: ${e.message}`);
    }
  }

  throw new Error(`Unexpected data type: ${typeof data}`);
}

// --- HELPER FUNCTION: Clean text content ---
function cleanText(text) {
  if (!text || typeof text !== 'string') return text;

  return text
    // Remove escaped quotes
    .replace(/\\"/g, '"')
    .replace(/\\'/g, "'")
    // Remove extra backslashes
    .replace(/\\\\/g, '\\')
    // Remove markdown bold/italic
    .replace(/\*\*(.+?)\*\*/g, '$1')
    .replace(/\*(.+?)\*/g, '$1')
    .replace(/__(.+?)__/g, '$1')
    .replace(/_(.+?)_/g, '$1')
    // Clean up extra spaces
    .replace(/\s+/g, ' ')
    .trim();
}

// --- HELPER FUNCTION: Recursively clean an object ---
function deepCleanObject(obj) {
  if (obj === null || obj === undefined) return obj;

  if (typeof obj === 'string') {
    return cleanText(obj);
  }

  if (Array.isArray(obj)) {
    return obj.map(item => deepCleanObject(item));
  }

  if (typeof obj === 'object') {
    const cleaned = {};
    for (const [key, value] of Object.entries(obj)) {
      cleaned[key] = deepCleanObject(value);
    }
    return cleaned;
  }

  return obj;
}

// --- 1. EXTRACT AND PARSE ---
let rawOutput = input.output || input.json || input.text || input;
console.log('Raw output type:', typeof rawOutput);

let parsedData;
try {
  parsedData = extractJSON(rawOutput);
  console.log('✓ Successfully extracted JSON');
} catch (error) {
  console.error('✗ Failed to extract JSON:', error.message);
  throw error;
}

// --- 2. VALIDATE REQUIRED FIELDS ---
const requiredFields = ['student_id', 'source_assessment_id', 'profile_json', 'summary_text'];
const missingFields = requiredFields.filter(field => !parsedData[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

if (typeof parsedData.profile_json !== 'object') {
  throw new Error('profile_json must be an object');
}

console.log('✓ All required fields present');

// --- 3. DEEP CLEAN ALL TEXT CONTENT ---
const cleanedProfileJson = deepCleanObject(parsedData.profile_json);
const cleanedSummary = cleanText(parsedData.summary_text);

console.log('✓ Text content cleaned');

// --- 4. BUILD FINAL RECORD ---
const generatorMeta = {
  llm_version: cleanedProfileJson.llm_version || 'gpt-4o-long_v1',
  generated_at: cleanedProfileJson.generated_at || new Date().toISOString(),
  workflow_id: $workflow.id || null,
  execution_id: $execution.id || null,
  processing_timestamp: new Date().toISOString()
};

const supabaseRecord = {
  student_id: parsedData.student_id,
  profile_json: cleanedProfileJson,
  summary_text: cleanedSummary,
  source_assessment_id: parsedData.source_assessment_id || null,
  generator_meta: generatorMeta
};

console.log('=== Parse Long Profile - SUMMARY ===');
console.log('Student ID:', supabaseRecord.student_id);
console.log('Summary preview:', cleanedSummary.substring(0, 100));
console.log('✓ Parse complete');

return { json: supabaseRecord };

// ============================================================================
// PARSE SHORT PROFILE - UPDATED TO HANDLE ARRAY OUTPUT
// ============================================================================

const input = $input.first().json;
console.log('=== Parse Short Profile - START ===');
console.log('Input type:', typeof input);
console.log('Input keys:', Object.keys(input));

// --- HELPER FUNCTION: Extract JSON from any format ---
function extractJSON(data) {
  let jsonString;

  // If already an object or array, return it
  if (typeof data === 'object' && data !== null) {
    return data;
  }

  // If string, clean it aggressively
  if (typeof data === 'string') {
    jsonString = data;

    // Remove all markdown code block markers
    jsonString = jsonString.replace(/```(?:json|javascript|js)?\s*/gi, '');
    jsonString = jsonString.replace(/```\s*/g, '');

    // Remove leading "json" or "JSON" text
    jsonString = jsonString.replace(/^json\s*/i, '');

    // Trim whitespace
    jsonString = jsonString.trim();

    // Find the first opening bracket (array or object)
    const firstBracket = Math.min(
      jsonString.indexOf('{') !== -1 ? jsonString.indexOf('{') : Infinity,
      jsonString.indexOf('[') !== -1 ? jsonString.indexOf('[') : Infinity
    );

    if (firstBracket === Infinity) {
      throw new Error('No opening bracket found in agent output');
    }

    // Determine if it's an array or object
    const isArray = jsonString[firstBracket] === '[';
    const openChar = isArray ? '[' : '{';
    const closeChar = isArray ? ']' : '}';

    // Match brackets to find the complete JSON
    let bracketCount = 0;
    let lastBracket = -1;

    for (let i = firstBracket; i < jsonString.length; i++) {
      if (jsonString[i] === openChar) bracketCount++;
      if (jsonString[i] === closeChar) {
        bracketCount--;
        if (bracketCount === 0) {
          lastBracket = i;
          break;
        }
      }
    }

    if (lastBracket === -1) {
      throw new Error('No matching closing bracket found');
    }

    jsonString = jsonString.substring(firstBracket, lastBracket + 1);

    // Parse the cleaned JSON
    try {
      return JSON.parse(jsonString);
    } catch (e) {
      console.error('JSON Parse Error:', e.message);
      console.error('Cleaned JSON (first 500 chars):', jsonString.substring(0, 500));
      throw new Error(`Failed to parse JSON: ${e.message}`);
    }
  }

  throw new Error(`Unexpected data type: ${typeof data}`);
}

// --- HELPER FUNCTION: Clean text content ---
function cleanText(text) {
  if (!text || typeof text !== 'string') return text;

  return text
    // Remove escaped quotes
    .replace(/\\"/g, '"')
    .replace(/\\'/g, "'")
    // Remove extra backslashes
    .replace(/\\\\/g, '\\')
    // Remove markdown bold/italic
    .replace(/\*\*(.+?)\*\*/g, '$1')
    .replace(/\*(.+?)\*/g, '$1')
    .replace(/__(.+?)__/g, '$1')
    .replace(/_(.+?)_/g, '$1')
    // Clean up extra spaces
    .replace(/\s+/g, ' ')
    .trim();
}

// --- HELPER FUNCTION: Recursively clean an object ---
function deepCleanObject(obj) {
  if (obj === null || obj === undefined) return obj;

  if (typeof obj === 'string') {
    return cleanText(obj);
  }

  if (Array.isArray(obj)) {
    return obj.map(item => deepCleanObject(item));
  }

  if (typeof obj === 'object') {
    const cleaned = {};
    for (const [key, value] of Object.entries(obj)) {
      cleaned[key] = deepCleanObject(value);
    }
    return cleaned;
  }

  return obj;
}

// --- 1. EXTRACT RAW OUTPUT ---
let rawOutput = input.output || input.json || input.text || input;
console.log('Raw output type:', typeof rawOutput);

// --- 2. HANDLE ARRAY WRAPPER ---
// The Student Short Profile agent returns an array with one item containing an "output" property
if (Array.isArray(rawOutput) && rawOutput.length > 0) {
  console.log('✓ Detected array output');
  rawOutput = rawOutput[0].output || rawOutput[0];
}

// --- 3. PARSE THE PROFILES ARRAY ---
let profilesArray;
try {
  profilesArray = extractJSON(rawOutput);
  console.log('✓ Successfully extracted JSON');
  console.log('Profiles array type:', Array.isArray(profilesArray) ? 'array' : typeof profilesArray);
  console.log('Number of profiles:', Array.isArray(profilesArray) ? profilesArray.length : 'N/A');
} catch (error) {
  console.error('✗ Failed to extract JSON:', error.message);
  throw error;
}

// Ensure we have an array
if (!Array.isArray(profilesArray)) {
  // If it's a single profile, wrap it in an array
  profilesArray = [profilesArray];
}

console.log(`✓ Processing ${profilesArray.length} profiles`);

// --- 4. VALIDATE AND PROCESS EACH PROFILE ---
const supabaseRecords = [];

for (let i = 0; i < profilesArray.length; i++) {
  const profile = profilesArray[i];
  console.log(`\n--- Processing Profile ${i + 1}/${profilesArray.length} ---`);

  // Validate required fields
  const requiredFields = ['student_id', 'context_key', 'short_profile'];
  const missingFields = requiredFields.filter(field => !profile[field]);

  if (missingFields.length > 0) {
    console.warn(`⚠ Skipping profile ${i + 1}: Missing fields: ${missingFields.join(', ')}`);
    continue;
  }

  if (typeof profile.short_profile !== 'object') {
    console.warn(`⚠ Skipping profile ${i + 1}: short_profile is not an object`);
    continue;
  }

  console.log('✓ Profile validated');
  console.log('  Student ID:', profile.student_id);
  console.log('  Context:', profile.context_key);

  // Deep clean all text content
  const cleanedShortProfile = deepCleanObject(profile.short_profile);
  const cleanedSummary = cleanText(profile.summary_text || profile.short_profile.quick_summary);

  // Build final record
  const generatorMeta = {
    llm_version: cleanedShortProfile.llm_version || 'gpt-4o-short_v1',
    generated_at: cleanedShortProfile.generated_at || new Date().toISOString(),
    workflow_id: $workflow.id || null,
    execution_id: $execution.id || null,
    processing_timestamp: new Date().toISOString(),
    score_confidence: cleanedShortProfile.score_confidence || null
  };

  const supabaseRecord = {
    student_id: profile.student_id,
    context_key: profile.context_key,
    short_profile: cleanedShortProfile,
    summary_text: cleanedSummary,
    generator_meta: generatorMeta
  };

  supabaseRecords.push(supabaseRecord);
  console.log('✓ Record prepared for:', profile.context_key);
}

console.log('\n=== Parse Short Profile - SUMMARY ===');
console.log('Total profiles processed:', supabaseRecords.length);
console.log('Contexts:', supabaseRecords.map(r => r.context_key).join(', '));
console.log('✓ Parse complete');

// Return array of records - each will be inserted separately
return supabaseRecords.map(record => ({ json: record }));
