  const item = items[i];

  try {
    // STEP 1: Parse OpenAI response structure
    // Response is an array: [{index: 0, message: {content: "..."}, ...}]
    const responseArray = Array.isArray(item.json) ? item.json : [item.json];
    const firstResponse = responseArray[0];

    if (!firstResponse || !firstResponse.message || !firstResponse.message.content) {
      throw new Error('Invalid OpenAI response structure');
    }

    let aiContent = firstResponse.message.content;

    // STEP 2: Extract JSON from markdown code blocks
    // Content format: ```json\n{...}\n```
    const jsonMatch = aiContent.match(/```(?:json)?\s*([\s\S]*?)\s*```/);
    if (jsonMatch) {
      aiContent = jsonMatch[1].trim();
    }

    // Parse the JSON
    const proposalData = JSON.parse(aiContent);

    // STEP 3: Get original chunk content from Limit node
    const limitNodeData = $('Loop Over Items1').all();

    if (!limitNodeData || !limitNodeData[i]) {
      throw new Error(`Cannot find chunk ${i} from Limit node`);
    }

    const originalChunk = limitNodeData[i].json;
    const chunkContent = originalChunk.content || '';

    if (!chunkContent) {
      throw new Error('Chunk content is empty');
    }

    // STEP 4: Build metadata object
    const metadata = {
      // From AI proposal
      proposal: proposalData.proposal || '',
      rationale: proposalData.rationale || '',
      priority: proposalData.priority || 'medium',
      follow_up: proposalData.follow_up || false,

      // From original chunk
      chunkNumber: originalChunk.chunkNumber || i + 1,
      chunkTitle: originalChunk.title || '',
      topics: Array.isArray(originalChunk.topics) ? originalChunk.topics.join(', ') : '',
      keyPoints: Array.isArray(originalChunk.keyPoints) ? originalChunk.keyPoints.join(' | ') : '',
      characterCount: originalChunk.characterCount || chunkContent.length,

      // System metadata
      timestamp: new Date().toISOString(),
      source: 'rag-doc-to-db-workflow'
    };

    // STEP 5: Create Supabase-ready output
    outputItems.push({
      json: {
        pageContent: chunkContent,
        metadata: JSON.stringify(metadata)
      }
    });

    console.log(`âœ… Successfully formatted chunk ${i + 1}`);

  } catch (error) {
    console.error(`âŒ Error processing item ${i}:`, error.message);

    // Fallback: try to get original content
    let fallbackContent = 'Error: Could not process chunk';
    try {
      const limitNodeData = $('Limit1').all();
      if (limitNodeData && limitNodeData[i]) {
        fallbackContent = limitNodeData[i].json.content || fallbackContent;
      }
    } catch (e) {
      console.error('Fallback also failed:', e.message);
    }

    // Output with error metadata
    outputItems.push({
      json: {
        pageContent: fallbackContent,
        metadata: JSON.stringify({
          error: error.message,
          errorStack: error.stack,
          timestamp: new Date().toISOString(),
          source: 'rag-doc-to-db-workflow-error'
        })
      }
    });
  }
}

console.log(`Processed ${outputItems.length} items total`);
