// Parse the OpenAI chat completion response
const items = $input.all();
const results = [];

for (const item of items) {
  const response = item.json;

  // Extract the assistant's message content from the chat completion response
  let content = '';

  if (response.choices && response.choices[0] && response.choices[0].message) {
    // Standard /v1/chat/completions response structure
    content = response.choices[0].message.content;
  } else if (response.output && Array.isArray(response.output)) {
    // Handle array wrapper if present
    if (typeof response.output[0] === 'object' && response.output[0].output) {
      content = response.output[0].output;
    } else if (typeof response.output[0] === 'string') {
      content = response.output[0];
    } else {
      content = JSON.stringify(response.output[0]);
    }
  } else if (typeof response === 'string') {
    content = response;
  } else if (response.data && response.data.content) {
    content = response.data.content;
  }

  // Clean up escaped characters if present
  if (typeof content === 'string') {
    // Remove escaped newlines and replace with actual newlines
    content = content.replace(/\\n/g, '\n');
    // Remove escaped quotes
    content = content.replace(/\\"/g, '"');
    // Remove escaped backslashes
    content = content.replace(/\\\\/g, '\\');
  }

  results.push({
    json: {
      assessment: content,
      originalResponse: response
    }
  });
}

return results;

