// --- Clean and format AI output for Google Docs ---

let memo = $input.item.json.output || "";

// Convert **bold** to <b>...</b> for Docs
memo = memo.replace(/\*\*(.*?)\*\*/g, "<b>$1</b>");

// Convert bullet points and lists to readable Docs format
memo = memo
  .replace(/\n•/g, "\n•")        // Keep bullets consistent
  .replace(/\n\d\./g, "\n\n$&")  // Add spacing before numbered lists
  .replace(/\n{2,}/g, "\n\n")    // Normalize double line breaks
  .trim();

// Return structured object
return {
  json: {
    memoContent: memo
  }
};

// Get founder details from Fillout Trigger
const firstName = $node["Fillout Trigger"].json.questions["xpbS"]["value"];
const lastName = $node["Fillout Trigger"].json.questions["4ma5"]["value"];
const email = $node["Fillout Trigger"].json.questions["8Etk"]["value"];

// Get current date
const now = new Date();
const months = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"];
const currentMonth = months[now.getMonth()];
const currentYear = now.getFullYear();

// Get AI-generated memo content
const memoContent = $input.item.json.output;

// Return data for document creation
return {
  json: {
    memoContent: memoContent,
    firstName: firstName,
    lastName: lastName,
    email: email,
    documentTitle: `${firstName} ${lastName} - Investment Memo`,
    documentDate: `${currentMonth}, ${currentYear}`
  }
};

