// Get document ID
const documentId = $('Get a document').item.json.documentId;

// Get data from Format Investment Memo
const formatData = $('Format Investment Memo').item.json;
const firstName = formatData.firstName || '';
const lastName = formatData.lastName || '';
const email = formatData.email || '';
const documentTitle = formatData.documentTitle || '';
const documentDate = formatData.documentDate || '';

// Clean memo content - remove any HTML tags
let memoContent = formatData.memoContent || '';
memoContent = memoContent.replace(/<\/?b>/g, '').replace(/<\/?i>/g, '');

// Build text replacements
const replacements = [
  {
    replaceAllText: {
      containsText: { text: "{{first name}}", matchCase: true },
      replaceText: firstName
    }
  },
  {
    replaceAllText: {
      containsText: { text: "{{last name}}", matchCase: true },
      replaceText: lastName
    }
  },
  {
    replaceAllText: {
      containsText: { text: "{{current month}}, {{current year}}", matchCase: true },
      replaceText: documentDate
    }
  },
  {
    replaceAllText: {
      containsText: { text: "{{user's email}}", matchCase: true },
      replaceText: email
    }
  },
  {
    replaceAllText: {
      containsText: { text: "{{document title}}", matchCase: true },
      replaceText: documentTitle
    }
  },
  {
    replaceAllText: {
      containsText: { text: "{{CONTENT}}", matchCase: true },
      replaceText: memoContent
    }
  }
];

// Define ALL headings that should be bold and larger
const mainHeadings = [
  "Problem Understanding",
  "Initial Customer Profile Understanding",
  "Proposed Solution",
  "Rationale for Solution",
  "Key Risks",
  "Why We Think This Can Succeed",
  "Recommendation"
];

// Define sub-headings within sections (these appear after section headings)
const subHeadings = [
  "Market problem description:",
  "Problem validation evidence:",
  "Problem significance:",
  "Target primary customer profile:",
  "Pain points:",
  "Customer journey:",
  "Recommended solution overview:",
  "Competitive advantage:",
  "Founder-market fit:",
  "Technical feasibility:",
  "Business model viability:",
  "Market opportunity:",
  "Main reasons this may not work:",
  "Critical unknowns to validate:",
  "Founder strengths:",
  "Comparable validation:",
  "Specific reasons Jan can win:",
  "Next steps for Explore stage (recommended):"
];

// Format main headings (bold + 12pt)
const mainHeadingRequests = mainHeadings.map(heading => ({
  replaceAllText: {
    containsText: {
      text: heading,
      matchCase: true
    },
    replaceText: heading,
    textStyle: {
      bold: true,
      fontSize: {
        magnitude: 12,
        unit: "PT"
      }
    }
  }
}));

// Format sub-headings (bold + 11pt, same as body)
const subHeadingRequests = subHeadings.map(heading => ({
  replaceAllText: {
    containsText: {
      text: heading,
      matchCase: true
    },
    replaceText: heading,
    textStyle: {
      bold: true,
      fontSize: {
        magnitude: 11,
        unit: "PT"
      }
    }
  }
}));

// Combine all requests
const allRequests = [...replacements, ...mainHeadingRequests, ...subHeadingRequests];

return {
  json: {
    documentId: documentId,
    requests: allRequests
  }
};
