// Enhanced normalization with deduplication and date sorting

const allLeadsList = [];
const seenLinks = new Set(); // Track unique links to avoid duplicates

// Helper function to parse relative dates and convert to timestamp
function parseRelativeDate(dateString) {
    if (!dateString || dateString === 'Date not available') {
        return new Date(0); // Very old date for items without dates
    }

    const now = new Date();
    const lowerDate = dateString.toLowerCase();

    // Handle "X hours ago" or "X hour ago"
    if (lowerDate.includes('hour')) {
        const hours = parseInt(lowerDate.match(/\d+/)?.[0] || 24);
        return new Date(now - hours * 60 * 60 * 1000);
    }

    // Handle "X days ago" or "X day ago"
    if (lowerDate.includes('day')) {
        const days = parseInt(lowerDate.match(/\d+/)?.[0] || 30);
        return new Date(now - days * 24 * 60 * 60 * 1000);
    }

    // Handle "X weeks ago" or "X week ago"
    if (lowerDate.includes('week')) {
        const weeks = parseInt(lowerDate.match(/\d+/)?.[0] || 4);
        return new Date(now - weeks * 7 * 24 * 60 * 60 * 1000);
    }

    // Handle "X months ago" or "X month ago"
    if (lowerDate.includes('month')) {
        const months = parseInt(lowerDate.match(/\d+/)?.[0] || 6);
        return new Date(now - months * 30 * 24 * 60 * 60 * 1000);
    }

    // Try to parse as actual date
    const parsedDate = new Date(dateString);
    if (!isNaN(parsedDate.getTime())) {
        return parsedDate;
    }

    return new Date(0); // Default to very old date if parsing fails
}

// Process all items from all merged scrapers
for (const item of $input.all()) {
    const rawData = item.json;

    // Access the 'organic' array, which holds the actual search results
    const organicResults = rawData.organic;

    if (Array.isArray(organicResults)) {
        // Iterate over each individual lead object
        for (const lead of organicResults) {

            // Skip if this link has already been processed (remove duplicates)
            if (seenLinks.has(lead.link)) {
                continue;
            }

            // Only process if required fields exist
            if (lead.title && lead.link && lead.snippet) {

                // Mark this link as seen
                seenLinks.add(lead.link);

                // Parse the date for sorting
                const parsedDate = parseRelativeDate(lead.date || 'Date not available');

                // Add to our results list
                allLeadsList.push({
                    Title: lead.title,
                    Link: lead.link,
                    Snippet: lead.snippet,
                    Date_String: lead.date || 'Date not available',
                    Parsed_Date: parsedDate,
                    Timestamp: parsedDate.getTime() // For easier sorting
                });
            }
        }
    }
}

// Sort by date - NEWEST FIRST (latest to oldest)
allLeadsList.sort((a, b) => b.Timestamp - a.Timestamp);

// Remove the Timestamp field before stringifying (keep it clean)
const cleanedLeads = allLeadsList.map(lead => ({
    Title: lead.Title,
    Link: lead.Link,
    Snippet: lead.Snippet,
    Date_String: lead.Date_String
}));

// Stringify the sorted and cleaned list
const stringifiedLeads = JSON.stringify(cleanedLeads, null, 2); 

return [
    {
        json: {
            all_leads_data: stringifiedLeads,
            total_leads: cleanedLeads.length,
            latest_post_date: allLeadsList[0]?.Date_String || 'No posts found',
            oldest_post_date: allLeadsList[allLeadsList.length - 1]?.Date_String || 'No posts found'
        }
    }
];

// n8n Code Node to parse AI agent output and format for Google Sheets

const inputData = $input.all();
let allRecords = [];

for (const item of inputData) {
  try {
    let outputText = item.json.output;

    // Remove markdown code block markers if present
    if (outputText.includes('```json')) {
      outputText = outputText.replace(/```json\n?/g, '').replace(/```\n?/g, '');
    }

    // Parse the JSON string
    const parsedData = JSON.parse(outputText.trim());

    // Process each record in the parsed array
    for (const record of parsedData) {
      const formattedRecord = {
        title: record.Title || '',
        details: record.Details || '',
        platform: record.Platform || '',
        post_url: record['Post Url'] || '',
        account_url: record['Platform Url'] || '',
        contact: record['Contact Info'] || '',
        budget: record.Budget || '',
        handle: record.Handle || '',
        date_posted: record['Date Posted'] || ''
      };

      allRecords.push(formattedRecord);
    }

  } catch (error) {
    console.error('Error parsing item:', error.message);
  }
}

return allRecords.map(record => ({ json: record }));

