const items = $input.all();

return items.map(item => {
  const emailAddress = item.json["Email Address"];
  const domain = emailAddress.substring(emailAddress.indexOf('@') + 1);

  return {
    json: {
      first_name: item.json["First Name"],
      last_name: item.json["Last Name"],
      email_addresses: [{
        email_address: emailAddress
      }],
      domain: domain
    }
  };
});

