parameters.jsCode:
// This single node combines the functionality of Split Out1, Edit Fields1, and Code1 nodes
const items = $input.all();

// Process each item from the input
const results = [];

// First, check if we're getting output from AI with people structure
items.forEach(item => {
  // Handle the Split Out1 functionality - extract people from output if it exists
  const peopleArray = item.json?.output?.people || [item.json];

  // Process each person in the array
  peopleArray.forEach(person => {
    // Extract the fields we need (Edit Fields1 functionality)
    const fullName = person['full name'] || '';
    const linkedinUrl = person['LinkedIn URL'] || '';
    const jobTitle = person['job title'] || '';
    const domain = person.domain || '';

    // Split the full name (Code1 functionality)
    const nameParts = fullName.split(' ');
    const firstName = nameParts[0] || '';
    const lastName = nameParts.length > 1 ? nameParts.slice(1).join(' ') : '';

    // Return complete processed record
    results.push({
      json: {
        'full name': fullName,
        'LinkedIn URL': linkedinUrl,
        'job title': jobTitle,
        domain: domain,
        firstName: firstName,
        lastName: lastName
      }
    });
  });
});

return results;

