// n8n Code Node - Extract Video ID from YouTube URL
// Input: YouTube URL
// Output: Video ID

const youtubeUrl = $('ğŸ“Š Get Ready Videos from Sheet ').first().json['Youtube Video Link'];

if (!youtubeUrl) {
  throw new Error('No YouTube URL provided');
}

function extractVideoId(url) {
  const patterns = [
    /(?:youtube\.com\/watch\?v=)([^&\n?#]+)/, // youtube.com/watch?v=
    /(?:youtu\.be\/)([^&\n?#]+)/, // youtu.be/
    /(?:youtube\.com\/embed\/)([^&\n?#]+)/, // youtube.com/embed/
    /(?:youtube\.com\/v\/)([^&\n?#]+)/, // youtube.com/v/
    /^([a-zA-Z0-9_-]{11})$/ // Direct video ID
  ];

  for (const pattern of patterns) {
    const match = url.match(pattern);
    if (match && match[1]) {
      return match[1];
    }
  }

  throw new Error('Could not extract video ID from URL');
}

try {
  const videoId = extractVideoId(youtubeUrl);

  return {
    video_id: videoId,
    original_url: youtubeUrl,
    success: true
  };

} catch (error) {
  return {
    error: error.message,
    original_url: youtubeUrl,
    success: false
  };
}

