// Get the transcript data from the previous node
const transcriptData = $input.first().json;

// Initialize variable to store clean transcript
let cleanTranscript = '';

// Check if transcript exists and is an array
if (transcriptData.transcript && Array.isArray(transcriptData.transcript)) {

  // Extract only the text from each segment and join them
  cleanTranscript = transcriptData.transcript
    .map(segment => {
      // Decode HTML entities (like &#39; to ')
      let text = segment.text || '';

      // Replace common HTML entities
      text = text
        .replace(/&#39;/g, "'")
        .replace(/&quot;/g, '"')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&nbsp;/g, ' ');

      return text;
    })
    .join(' ')  // Join all text segments with a space
    .trim();    // Remove leading/trailing whitespace

} else if (typeof transcriptData.transcript === 'string') {
  // If transcript is already a string, just clean it
  cleanTranscript = transcriptData.transcript
    .replace(/&#39;/g, "'")
    .replace(/&quot;/g, '"')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&nbsp;/g, ' ')
    .trim();
}

// Return the formatted data with clean transcript
return [{
  json: {
    ...($json),  // Keep all existing fields
    'Transcript': cleanTranscript,  // Add/update the clean transcript
    transcriptLength: cleanTranscript.length,  // Optional: track length
    transcriptWordCount: cleanTranscript.split(' ').length  // Optional: track word count
  }
}];

// Log for debugging
console.log('Transcript extracted successfully');
console.log('Character count:', cleanTranscript.length);
console.log('Word count:', cleanTranscript.split(' ').length);
