// Get current date and 5 days ago
const now = new Date();
const fiveDaysAgo = new Date(now.getTime() - (5 * 24 * 60 * 60 * 1000));

// Filter reviews from the last 5 days
const recentReviews = $input.all().filter(item => {
  if (!item.json.createTime) return false;
  const reviewDate = new Date(item.json.createTime);
  return reviewDate >= fiveDaysAgo;
});

return recentReviews;

// Get existing Review IDs from Airtable
// Note: Airtable returns data as json.records[]
const airtableData = $('Get All Existing Review IDs').all();
const existingReviewIds = new Set();

// Extract Review IDs from the nested structure
airtableData.forEach(item => {
  if (item.json.records && Array.isArray(item.json.records)) {
    item.json.records.forEach(record => {
      if (record.fields && record.fields['Review ID']) {
        existingReviewIds.add(record.fields['Review ID']);
      }
    });
  }
});

// Get recent reviews from Google
const recentReviews = $('Filter Last 5 Days').all();

// Filter out reviews that already exist in Airtable
const newReviews = recentReviews.filter(item => {
  const reviewId = item.json.reviewId;
  return reviewId && !existingReviewIds.has(reviewId);
});

return newReviews;
