// Aggregate all LinkedIn connections from multiple pages
const allItems = $input.all();
const allConnections = [];

for (const item of allItems) {
  const elements = item.json.elements || [];
  allConnections.push(...elements);
}

// Create a lookup map of connections
const connectionMap = {};

for (const conn of allConnections) {
  if (!conn['to~']) continue;

  const profile = conn['to~'];
  const personId = profile.id;
  const vanityName = profile.vanityName || '';
  const firstName = profile.firstName?.localized?.en_US || '';
  const lastName = profile.lastName?.localized?.en_US || '';
  const fullName = `${firstName} ${lastName}`.trim();

  connectionMap[personId] = {
    personId,
    vanityName,
    firstName,
    lastName,
    fullName,
    linkedInUrl: vanityName ? `https://www.linkedin.com/in/${vanityName}/` : ''
  };
}

return [{
  json: {
    totalConnections: allConnections.length,
    connections: connectionMap,
    timestamp: new Date().toISOString()
  }
}];

// Match Airtable pending records with LinkedIn connections
const airtableRecords = $('Get Pending Connections from Airtable').all();
const connectionData = $input.first().json;
const connectionMap = connectionData.connections;

const matches = [];
const noMatches = [];

// Helper function to normalize strings for comparison
function normalize(str) {
  if (!str) return '';
  return str.toLowerCase().trim().replace(/[^a-z0-9]/g, '');
}

for (const record of airtableRecords) {
  const data = record.json;
  let matched = false;
  let matchMethod = null;
  let matchedConnection = null;

  // Method 1: Match by LinkedIn Person ID (most reliable)
  if (data.LinkedIn_Person_ID && connectionMap[data.LinkedIn_Person_ID]) {
    matched = true;
    matchMethod = 'person_id';
    matchedConnection = connectionMap[data.LinkedIn_Person_ID];
  }

  // Method 2: Match by LinkedIn vanity URL
  if (!matched && data.Employee_LinkedIn_URL) {
    const urlParts = data.Employee_LinkedIn_URL.split('/in/');
    if (urlParts.length > 1) {
      const vanityName = urlParts[1].replace('/', '').toLowerCase();

      for (const [personId, conn] of Object.entries(connectionMap)) {
        if (conn.vanityName && normalize(conn.vanityName) === normalize(vanityName)) {
          matched = true;
          matchMethod = 'vanity_url';
          matchedConnection = conn;
          break;
        }
      }
    }
  }

  // Method 3: Match by full name (fuzzy)
  if (!matched && data.Employee_Full_Name) {
    const airtableName = normalize(data.Employee_Full_Name);

    for (const [personId, conn] of Object.entries(connectionMap)) {
      const linkedinName = normalize(conn.fullName);

      if (airtableName === linkedinName) {
        matched = true;
        matchMethod = 'exact_name';
        matchedConnection = conn;
        break;
      }
    }
  }

  if (matched && matchedConnection) {
    matches.push({
      json: {
        ...data,
        matched: true,
        matchMethod,
        LinkedIn_Person_ID: matchedConnection.personId,
        LinkedIn_Full_Name: matchedConnection.fullName,
        LinkedIn_Profile_URL: matchedConnection.linkedInUrl,
        checkedAt: new Date().toISOString()
      }
    });
  } else {
    noMatches.push({
      json: {
        ...data,
        matched: false,
        checkedAt: new Date().toISOString()
      }
    });
  }
}

console.log(`Matched: ${matches.length}, Not matched: ${noMatches.length}`);

return matches;

const updated = $input.all();

return [{
  json: {
    success: true,
    timestamp: new Date().toISOString(),
    connectionsChecked: $('Get Pending Connections from Airtable').all().length,
    connectionsAccepted: updated.length,
    message: `Updated ${updated.length} connection(s) to accepted status`
  }
}];
