// Extract company information from search results
const allData = [];

for (const item of $input.all()) {
  const searchResults = item.json.organic || [];
  const companyName = $('Extract Company Data').all().find(x => x.json.row_number === item.json.row_number)?.json.company_name || 'Unknown';

  let website = '';
  let description = '';
  let industry = '';

  // Find company website and info
  for (const result of searchResults) {
    const title = result.title?.toLowerCase() || '';
    const snippet = result.snippet?.toLowerCase() || '';
    const link = result.link || '';

    // Look for official website
    if (title.includes(companyName.toLowerCase()) && 
        (link.includes('.com') || link.includes('.org') || link.includes('.net'))) {
      if (!website || link.length < website.length) {
        website = link;
      }
    }

    // Extract description and industry info
    if (snippet.includes('company') || snippet.includes('business')) {
      if (!description && snippet.length > 50) {
        description = result.snippet;
      }
    }
  }

  // Extract domain from website
  let domain = '';
  if (website) {
    try {
      const url = new URL(website);
      domain = url.hostname.replace('www.', '');
    } catch (e) {
      domain = website.replace(/https?:\/\/(www\.)?/, '').split('/')[0];
    }
  }

  allData.push({
    company_name: companyName,
    website: website || 'Not found',
    domain: domain || 'Not found',
    description: description || 'Not found',
    industry: industry || 'To be determined',
    row_number: item.json.row_number || 1
  });
}

return allData;

// Extract CEO LinkedIn profile from search results
const allData = [];

for (const item of $input.all()) {
  const searchResults = item.json.organic || [];
  const prevData = $('Extract Company Details').all().find(x => x.json.row_number === item.json.row_number)?.json || {};

  let ceoName = '';
  let ceoLinkedin = '';
  let ceoTitle = '';

  // Look for LinkedIn profiles of CEOs
  for (const result of searchResults) {
    const title = result.title || '';
    const snippet = result.snippet || '';
    const link = result.link || '';

    // Check if it's a LinkedIn profile
    if (link.includes('linkedin.com/in/')) {
      // Look for CEO, Founder, President keywords
      const titleLower = title.toLowerCase();
      const snippetLower = snippet.toLowerCase();

      if (titleLower.includes('ceo') || titleLower.includes('chief executive') ||
          titleLower.includes('founder') || titleLower.includes('president') ||
          snippetLower.includes('ceo') || snippetLower.includes('chief executive')) {

        // Extract name from title (usually before the | or - separator)
        const nameMatch = title.match(/^([^|\-]+)/);
        if (nameMatch) {
          ceoName = nameMatch[1].trim();
          ceoLinkedin = link;

          // Extract title
          if (snippetLower.includes('ceo')) {
            ceoTitle = 'CEO';
          } else if (snippetLower.includes('founder')) {
            ceoTitle = 'Founder';
          } else if (snippetLower.includes('president')) {
            ceoTitle = 'President';
          } else {
            ceoTitle = 'Executive';
          }

          break; // Found the CEO, stop looking
        }
      }
    }
  }

  allData.push({
    ...prevData,
    ceo_name: ceoName || 'Not found',
    ceo_title: ceoTitle || 'Not found',
    ceo_linkedin: ceoLinkedin || 'Not found'
  });
}

return allData;

// Extract CEO email from Hunter.io results
const allData = [];

for (const item of $input.all()) {
  const hunterData = item.json.data || {};
  const emails = hunterData.emails || [];
  const prevData = $('Extract CEO Information').all().find(x => x.json.row_number === item.json.row_number)?.json || {};
  const ceoName = prevData.ceo_name;

  let ceoEmail = '';
  let alternativeEmails = [];

  // If we have the CEO name, try to find their specific email
  if (ceoName && ceoName !== 'Not found') {
    const ceoNameParts = ceoName.toLowerCase().split(' ');

    for (const emailData of emails) {
      const email = emailData.value || '';
      const firstName = (emailData.first_name || '').toLowerCase();
      const lastName = (emailData.last_name || '').toLowerCase();
      const position = (emailData.position || '').toLowerCase();

      // Check if this email belongs to the CEO
      if (position.includes('ceo') || position.includes('chief executive') ||
          position.includes('founder') || position.includes('president')) {
        ceoEmail = email;
        break;
      }

      // Try to match by name
      if (firstName && lastName && ceoNameParts.length >= 2) {
        if (ceoNameParts.includes(firstName) && ceoNameParts.includes(lastName)) {
          ceoEmail = email;
          break;
        }
      }
    }
  }

  // If no specific CEO email found, get the first executive-level email
  if (!ceoEmail && emails.length > 0) {
    for (const emailData of emails) {
      const position = (emailData.position || '').toLowerCase();
      if (position.includes('ceo') || position.includes('founder') || 
          position.includes('president') || position.includes('executive')) {
        ceoEmail = emailData.value;
        break;
      }
    }
  }

  // Collect alternative emails for backup
  alternativeEmails = emails.slice(0, 3).map(e => e.value).filter(e => e !== ceoEmail);

  allData.push({
    ...prevData,
    ceo_email: ceoEmail || 'Not found',
    alternative_emails: alternativeEmails,
    total_emails_found: emails.length
  });
}

return allData;

// Extract recent news about the company
const allData = [];

for (const item of $input.all()) {
  const newsResults = item.json.news || [];
  const prevData = $('Extract Email Information').all().find(x => x.json.row_number === item.json.row_number)?.json || {};

  let recentNews = [];
  let newsTopics = [];

  // Process news articles
  for (const article of newsResults.slice(0, 3)) {
    const title = article.title || '';
    const snippet = article.snippet || '';
    const link = article.link || '';
    const date = article.date || '';
    const source = article.source || '';

    if (title && snippet) {
      recentNews.push({
        title: title,
        snippet: snippet,
        link: link,
        date: date,
        source: source
      });

      // Extract key topics from title
      const titleLower = title.toLowerCase();
      if (titleLower.includes('funding') || titleLower.includes('investment')) {
        newsTopics.push('Funding/Investment');
      }
      if (titleLower.includes('partnership') || titleLower.includes('collaboration')) {
        newsTopics.push('Partnership');
      }
      if (titleLower.includes('product') || titleLower.includes('launch')) {
        newsTopics.push('Product Launch');
      }
      if (titleLower.includes('expansion') || titleLower.includes('growth')) {
        newsTopics.push('Expansion');
      }
      if (titleLower.includes('acquisition') || titleLower.includes('merger')) {
        newsTopics.push('M&A');
      }
    }
  }

  // Create news summary
  let newsSummary = '';
  if (recentNews.length > 0) {
    const latestNews = recentNews[0];
    newsSummary = `Latest: ${latestNews.title} - ${latestNews.snippet.substring(0, 150)}...`;
  }

  allData.push({
    ...prevData,
    recent_news: recentNews,
    news_summary: newsSummary || 'No recent news found',
    news_topics: [...new Set(newsTopics)], // Remove duplicates
    news_count: recentNews.length
  });
}

return allData;
