  },
  "input": [
    {
      "role": "user",
      "content": [
        {
          "type": "input_text",
          "text": {{ 
            JSON.stringify(
              (()=>{
                const founder_first_name = $node["Fillout Trigger"].json.questions["xpbS"]["value"];
                const founder_last_name = $node["Fillout Trigger"].json.questions["4ma5"]["value"];
                const q = $node["Fillout Trigger"].json.questions;
                const ids = [
                  "3jkv","mFMG","9po8","5HJE","qG1G","hN3D","rJ3u","tZ42","7wHz","hKVV",
                  "qFLN","mymB","9CiT","tcTa","dNQ5","chXn","wFSJ","8iZG","mRvX","9iJC",
                  "v8MM","gYjH","nCYJ","mJME","3BFW","7rP9","vGqM","umgN","8fKT"
                ];

                const lines = [`Following are the questions and their answers: \n\n Founder: ${founder_first_name} ${founder_last_name}`];
                for (const id of ids) {
                  if (!q || !q[id]) continue;
                  const name = q[id].name ?? id;
                  let val = q[id].value;
                  if (Array.isArray(val)) val = val.join(", ");
                  if (val === undefined || val === null) val = "";
                  lines.push(name);
                  lines.push(String(val));
                }
                return lines.join("\n\n");
              })()
            ) 
          }}
        }
      ]
    }
  ],
  "store": true
}

