for (const item of items) {
  const lighthouseData = item.json;

  if (lighthouseData && lighthouseData.lighthouseResult) {
    const audits = lighthouseData.lighthouseResult.audits;
    const categories = lighthouseData.lighthouseResult.categories;

    const outputData = {
      // General SEO Score
      finalUrl: lighthouseData.lighthouseResult.finalUrl,
      seoScore: categories.seo?.score * 100,

      // SEO Audit Details - All these audits have a score of 1 in your data, indicating "passed"
      // They are useful for confirming that core SEO elements are in place.
      documentTitle: {
        title: audits['document-title']?.title,
        score: audits['document-title']?.score,
      },
      metaDescription: {
        title: audits['meta-description']?.title,
        score: audits['meta-description']?.score,
      },
      httpStatusCode: {
        title: audits['http-status-code']?.title,
        score: audits['http-status-code']?.score,
      },
      robotsTxt: {
        title: audits['robots-txt']?.title,
        score: audits['robots-txt']?.score,
      },
      linkText: {
        title: audits['link-text']?.title,
        score: audits['link-text']?.score,
      },
      crawlableAnchors: {
        title: audits['crawlable-anchors']?.title,
        score: audits['crawlable-anchors']?.score,
      },
      pageIsCrawlable: {
        title: audits['is-crawlable']?.title,
        score: audits['is-crawlable']?.score,
      },
      imageAlts: {
        title: audits['image-alt']?.title,
        score: audits['image-alt']?.score,
      },
      hreflang: {
        title: audits['hreflang']?.title,
        score: audits['hreflang']?.score,
      },
    };

    return [{
      json: outputData
    }];
  }
}

for (const item of items) {
  const lighthouseData = item.json;

  if (lighthouseData && lighthouseData.lighthouseResult) {
    const audits = lighthouseData.lighthouseResult.audits;
    const categories = lighthouseData.lighthouseResult.categories;
    const loadingExperience = lighthouseData.loadingExperience;

    // A check to handle cases where 'performance' might not be in the categories
    const performanceCategory = categories.performance;

    if (!performanceCategory) {
      continue;
    }

    const outputData = {
      // General Scores & URL
      finalUrl: lighthouseData.lighthouseResult.finalUrl,
      performanceScore: performanceCategory.score * 100,

      // Core Web Vitals (Lab Data from Lighthouse)
      largestContentfulPaint_lab: audits['largest-contentful-paint']?.displayValue,
      cumulativeLayoutShift_lab: audits['cumulative-layout-shift']?.displayValue,
      totalBlockingTime_lab: audits['total-blocking-time']?.displayValue,
      speedIndex_lab: audits['speed-index']?.displayValue,
      firstContentfulPaint_lab: audits['first-contentful-paint']?.displayValue,
      interactive_lab: audits['interactive']?.displayValue,

      // Core Web Vitals (Real-World Data from Chrome User Experience Report)
      // Note: This data is often more reflective of actual user experience
      realWorldCLS_category: loadingExperience?.metrics?.CUMULATIVE_LAYOUT_SHIFT_SCORE?.category,
      realWorldLCP_category: loadingExperience?.metrics?.LARGEST_CONTENTFUL_PAINT_MS?.category,
      realWorldINP_category: loadingExperience?.metrics?.INTERACTION_TO_NEXT_PAINT?.category,
      realWorldFCP_category: loadingExperience?.metrics?.FIRST_CONTENTFUL_PAINT_MS?.category,

      // Key Opportunities & Actionable Recommendations
      renderBlockingResources: {
        title: audits['render-blocking-resources']?.title,
        savingsMs: audits['render-blocking-resources']?.details?.overallSavingsMs || 0,
        items: audits['render-blocking-resources']?.details?.items || []
      },
      imageDelivery: {
        title: audits['image-delivery-insight']?.title,
        savingsBytes: audits['image-delivery-insight']?.details?.overallSavingsBytes || 0,
        items: audits['image-delivery-insight']?.details?.items || []
      },
      longTasks: {
        title: audits['long-tasks']?.title,
        value: audits['long-tasks']?.displayValue,
        items: audits['long-tasks']?.details?.items || []
      },
      serverResponseTime: {
        title: audits['server-response-time']?.title,
        value: audits['server-response-time']?.displayValue,
        savingsMs: audits['server-response-time']?.details?.overallSavingsMs || 0
      },
      cachePolicy: {
        title: audits['uses-long-cache-ttl']?.title,
        savingsBytes: audits['uses-long-cache-ttl']?.details?.summary?.wastedBytes || 0,
        items: audits['uses-long-cache-ttl']?.details?.items || []
      },
      totalByteWeight: {
        title: audits['total-byte-weight']?.title,
        totalBytes: audits['total-byte-weight']?.numericValue || 0,
        items: audits['total-byte-weight']?.details?.items || []
      },
      preconnectedOrigins: {
        title: "Preconnect Origins",
        items: audits['network-dependency-tree-insight']?.details?.items.find(i => i.title === 'Preconnect candidates')?.value?.items || []
      }
    };

    return [{ json: outputData }];
  }
}

// Get all items from input
const items = $input.all();

// Merge all objects into a single data field and stringify
return [{
  json: {
    data: JSON.stringify({
      seo: items[0].json,
      performance: items[1].json,
      backlinks: items[2].json
    })
  }
}];

for (const item of items) {
  try {
    // Step 1: Parse the main JSON to get the 'output' string
    const outputString = item.json.output;

    // Step 2: Parse the 'output' string into a JSON array
    const findingsArray = JSON.parse(outputString);

    // Step 3: Return the array of findings for the next node
    return findingsArray.map(finding => ({ json: finding }));

  } catch (error) {
    // Handle any parsing errors here
    console.error("Error parsing AI agent output:", error);
    // You can choose to return an error item or just the original item
    return [{ json: { error: "Failed to parse AI agent output" } }];
  }
}
