// Extract first line from document_content
const content = $json.document_content || '';
const title = content.split('\n')[0].replace(/^#+\s*/, '');

// Return it as a field so next nodes can access it
return [
  {
    json: {
      ...$json,
      title_from_content: title
    }
  }
];

// Validate input structure
if (!$json || typeof $json !== 'object') {
  return [{
    json: {
      error: "Invalid input: $json is not an object",
      overall_score: 5
    }
  }];
}

// Check if content.parts exists
if (!$json.content || !$json.content.parts || !Array.isArray($json.content.parts) || $json.content.parts.length === 0) {
  return [{
    json: {
      ...$json,
      error: "Invalid data structure - expected content.parts array from Gemini",
      overall_score: 5
    }
  }];
}

// Check if text exists
if (!$json.content.parts[0].text) {
  return [{
    json: {
      ...$json,
      error: "No text found in content.parts[0]",
      overall_score: 5
    }
  }];
}

// Get the text that contains the JSON
const raw = $json.content.parts[0].text;

// Extract the JSON part between ```json and ```
const match = raw.match(/```json\s*([\s\S]*?)\s*```/);

if (!match) {
  // Try without code blocks
  try {
    const parsed = JSON.parse(raw);
    return [{
      json: {
        ...$json,
        overall_score: parsed.overall_score || 5,
        quality_report: parsed
      }
    }];
  } catch (e) {
    return [{
      json: {
        ...$json,
        error: "JSON block not found and raw text is not valid JSON",
        overall_score: 5
      }
    }];
  }
}

// Parse the extracted JSON
try {
  const parsed = JSON.parse(match[1]);

  // Return the overall_score AND preserve all existing data
  return [{
    json: {
      ...$json,
      overall_score: parsed.overall_score || 5,
      quality_report: parsed
    }
  }];
} catch (e) {
  return [{
    json: {
      ...$json,
      error: `Failed to parse quality score JSON: ${e.message}`,
      overall_score: 5
    }
  }];
}

// Get the form submission data
const formData = $('On form submission1').first().json;

// Get all merged data (SERP Research + SERP NEWS)
const mergedData = $input.all();

// Find SERP Research and SERP News data
const serpResearch = mergedData.find(item => item.json.organic || item.json.organic_results);
const serpNews = mergedData.find(item => item.json.news);

// Extract and simplify top 5 organic results (title + snippet only)
let simplifiedOrganic = [];
if (serpResearch && (serpResearch.json.organic || serpResearch.json.organic_results)) {
  const organicResults = serpResearch.json.organic || serpResearch.json.organic_results;
  simplifiedOrganic = organicResults
    .slice(0, 5)
    .map(result => ({
      title: result.title || '',
      snippet: result.snippet || ''
    }));
}

// Extract and simplify top 5 news results (title + snippet only)
let simplifiedNews = [];
if (serpNews && Array.isArray(serpNews.json.news)) {
  simplifiedNews = serpNews.json.news
    .slice(0, 5)
    .map(newsItem => ({
      title: newsItem.title || '',
      snippet: newsItem.snippet || ''
    }));
}

// Convert to human-readable text format
const organicText = simplifiedOrganic
  .map((item, index) => `${index + 1}. ${item.title}\n   ${item.snippet}`)
  .join('\n\n');

const newsText = simplifiedNews
  .map((item, index) => `${index + 1}. ${item.title}\n   ${item.snippet}`)
  .join('\n\n');

// Default reference content
const referenceContent = 'No reference content available';

// Combine everything into one output object
return [{
  json: {
    Keyword: formData['Main Keywords'],
    other_keywords: formData['Other Keywords'] || '',
    word_count: '1500',

    // Stringified versions for AI consumption
    organic_results: organicText || 'No organic results found',
    news: newsText || 'No news results found',
    reference_content: referenceContent,

    // Keep arrays for potential future use
    organic_results_array: simplifiedOrganic,
    news_array: simplifiedNews
  }
}];
