// Enhanced Parse & Route with Multiple Decision Points
const aiOutput = $input.item.json;
let aiResponse = (aiOutput.output || aiOutput.text || '').trim();

let routeDecision = {
  isComplete: false,
  needsProductCatalog: false,
  needsBoxCatalog: false,
  continueCollection: true,
  sessionData: null,
  aiResponse: aiResponse,
  originalInput: $json.chatInput || ''
};

// Check if user is asking about items/products
const userMessage = ($json.chatInput || '').toLowerCase();
const itemPatterns = [
  'show items', 'what items', 'what products', 'available items',
  'show catalog', 'list items', 'what do you have', 'show me products',
  'what can you include', 'what are the options'
];

const boxPatterns = [
  'box', 'packaging', 'container', 'gift box', 'packing'
];

// Detect user intent
routeDecision.needsProductCatalog = itemPatterns.some(p => userMessage.includes(p));
routeDecision.needsBoxCatalog = boxPatterns.some(p => userMessage.includes(p));

// Try to parse completion JSON
try {
  const jsonMatch = aiResponse.match(/\{[^}]+\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);

    if (parsed.status === 'DATA_COMPLETE') {
      // Validate all required fields
      if (parsed.budget && parsed.quantity && parsed.when && parsed.itemPreference !== undefined) {
        routeDecision.isComplete = true;
        routeDecision.continueCollection = false;
        routeDecision.sessionData = {
          budget: parsed.budget,
          quantity: parsed.quantity,
          when: parsed.when,
          itemPreference: parsed.itemPreference || 'no'
        };

        console.log('âœ… Data collection complete:', routeDecision.sessionData);
        aiResponse = 'DATA_COMPLETE'; // Hide JSON from user
      } else {
        console.warn('âš ï¸ Incomplete data in completion response');
      }
    }
  }
} catch (e) {
  // Not JSON, continue normal flow
  console.log('Continuing conversation...');
}

// Determine routing priority
if (routeDecision.isComplete) {
  routeDecision.route = 'complete';
} else if (routeDecision.needsProductCatalog) {
  routeDecision.route = 'catalog';
} else if (routeDecision.needsBoxCatalog) {
  routeDecision.route = 'boxes';
} else {
  routeDecision.route = 'continue';
}

return [{
  json: routeDecision
}];

// Prepare query to fetch catalog from RAG
const catalogQuery = "show all available products dry fruits nuts chocolates sweets diyas candles boxes packaging gift hampers festive items snacks trail mix granola almonds cashew";

return [{
  json: {
    catalogQuery: catalogQuery,
    requestType: "catalog",
    needsCatalog: true
  }
}];

// Prepare query to fetch box options from RAG
const boxQuery = "packaging box ratan jali wooden customized logo gift box container small medium large deluxe all sizes prices";

return [{
  json: {
    boxQuery: boxQuery,
    requestType: "boxes",
    needsBoxes: true
  }
}];

// Prepare Query for RAG Search
const sessionData = $json.sessionData;

if (!sessionData || !sessionData.budget) {
  throw new Error('Session data incomplete');
}

// Parse preferences and build search query
const prefs = (sessionData.itemPreference || 'no').toLowerCase();
let searchTerms = [];

// Build comprehensive search terms
if (prefs === 'no' || prefs === 'none') {
  // Search for popular items
  searchTerms = ['dry fruits', 'almonds', 'cashew', 'chocolate', 'diya', 'candle', 'gift', 'hamper', 'box', 'festive'];
} else {
  // Parse specific preferences
  if (prefs.includes('dry fruit') || prefs.includes('nut')) {
    searchTerms.push('almonds', 'cashew', 'pistachio', 'mixed dry fruits');
  }
  if (prefs.includes('diya')) {
    searchTerms.push('diya', 'brass diya', 'decorative diya');
  }
  if (prefs.includes('candle')) {
    searchTerms.push('candle', 'scented candle', 'floating candle');
  }
  if (prefs.includes('chocolate') || prefs.includes('sweet')) {
    searchTerms.push('chocolate', 'ferrero', 'dates', 'kaju katli');
  }
  if (prefs.includes('box')) {
    searchTerms.push('ratan jali', 'wooden box', 'packaging');
  }
}

// Always add hamper-related terms
searchTerms.push('gift hamper', 'festive', 'premium');

// Parse quantity for discount calculation
const quantity = parseInt(sessionData.quantity?.toString().replace(/[^0-9]/g, '')) || 0;

let discountPercent = 35;
let discountTier = 'Standard';

if (quantity >= 500) {
  discountPercent = 45;
  discountTier = 'MEGA';
} else if (quantity >= 200) {
  discountPercent = 40;
  discountTier = 'Premium';
}

const query = `gift hamper ${searchTerms.join(' ')} budget ${sessionData.budget} rupees festive collection`;

console.log('ðŸ” RAG Query:', query);
console.log('ðŸ’° Discount:', discountPercent + '%', '(' + discountTier + ')');

return [{
  json: {
    query: query,
    sessionData: {
      ...sessionData,
      quantity: quantity,
      discountPercent: discountPercent,
      discountTier: discountTier
    }
  }
}];

// Apply Discount and Format Final Response
const ragOutput = $json.output || $json.text || '';
const sessionData = $('Prepare RAG Query').first().json.sessionData;

const quantity = sessionData.quantity;
const discountPercent = sessionData.discountPercent;
const discountMultiplier = (100 - discountPercent) / 100;

// Clean the response
let finalResponse = ragOutput
  .replace(/\\n/g, '\n')
  .replace(/\\\*/g, '*')
  .trim();

// Apply discount to all prices
finalResponse = finalResponse.replace(/â‚¹\s*(\d+(?:,\d+)*(?:\.\d+)?)/g, (match, price) => {
  const original = parseFloat(price.replace(/,/g, ''));
  const discounted = Math.round(original * discountMultiplier);
  return `â‚¹${original} â†’ â‚¹${discounted}`;
});

// Add order summary
const orderSummary = `\n\nðŸ“‹ **Order Summary:**
â€¢ Quantity: ${quantity} identical hampers
â€¢ Discount Applied: ${discountPercent}% ${sessionData.discountTier}
â€¢ Delivery Date: ${sessionData.when}
â€¢ Status: âœ… Ready to process

ðŸŽŠ **Thank you for choosing Kiwi Gift Hampers!**

Would you like to:
1. Confirm this order
2. Modify the selection
3. Start a new order`;

finalResponse += orderSummary;

return [{
  json: {
    finalResponse: finalResponse,
    orderComplete: true,
    sessionData: sessionData
  }
}];

// Store Order in Database/Sheets for Analytics
const orderData = $json.sessionData;

const analyticsRecord = {
  timestamp: new Date().toISOString(),
  orderId: 'ORD-' + Date.now(),
  budget: orderData.budget,
  quantity: orderData.quantity,
  deliveryDate: orderData.when,
  preferences: orderData.itemPreference,
  discountPercent: orderData.discountPercent,
  discountTier: orderData.discountTier,
  totalValue: parseFloat(orderData.budget) * parseInt(orderData.quantity),
  status: 'completed'
};

console.log('ðŸ“Š Order Analytics:', analyticsRecord);

// In production, this would save to Google Sheets or database
return [{
  json: {
    analytics: analyticsRecord,
    success: true,
    message: 'Order recorded successfully'
  }
}];
