    "body_composition": {
      "current_status": "Based on Weight & BMI test",
      "key_metrics": ["Weight: 85kg", "Height: 178cm", "BMI Category: Overweight"],
      "targets": "Extract from coach notes (8-10 kg weight loss)"
    },
    "mobility": {
      "current_level": "Extract from coach observations",
      "key_metrics": ["Based on posture and movement notes"],
      "limitations": ["Any noted restrictions"]
    },
    "flexibility": {
      "current_level": "Extract from assessments",
      "key_metrics": ["Based on available data"],
      "areas_needing_work": ["From improvement areas"]
    }
  },

  "posture_and_form": {
    "observations": ["Extract from coach notes: 'Good posture observed during most movements'"],
    "form_quality": ["Extract from test responses: 'good' form quality", "Form breaks down after 12 reps"],
    "recommendations": ["Based on coach recommendations"]
  },

  "progress_milestones": [
    {
      "date": "{{ $json.first_assessment_date }}",
      "achievement": "Completed baseline assessment",
      "significance": "Established fitness baseline"
    }
  ],

  "coach_insights": {
    "behavioral_observations": ["Extract from {{ $json.latest_coach_notes }}: 'enthusiastic and cooperative'"],
    "performance_patterns": ["Extract strengths and weaknesses from test results and notes"],
    "recommendations": ["Extract from coach notes: 'start with modified exercises', etc."],
    "focus_areas": ["Based on {{ $json.assessments[0].focus_areas }}"]
  },

  "engagement_summary": {
    "attendance_pattern": "Based on {{ $json.redeemed_sessions }}/{{ $json.total_sessions }}",
    "session_utilization": "Calculate percentage",
    "responsiveness": "Infer from coach behavioral observations",
    "barriers": ["Any obstacles mentioned in notes"]
  },

  "key_considerations_for_coaches": [
    "Extract critical points from all coach notes",
    "Safety-critical modifications",
    "Key focus areas for training"
  ],

  "summary_snapshot": {
    "one_line": "Brief summary of current status",
    "current_phase": "{{ $json.latest_assessment_phase }}",
    "next_steps": ["Based on recommendations and progress percentage"]
  }
}

