// ==========================================
// IMPROVED JSON PARSER FOR AI AGENT OUTPUT
// ==========================================
// Handles markdown code blocks and cleans the output

const aiOutput = $input.first().json.output;

// Function to extract and parse JSON from AI response
function parseAIOutput(output) {
  try {
    // Method 1: Try direct JSON parse first
    try {
      return JSON.parse(output);
    } catch (e) {
      // Continue to other methods
    }

    // Method 2: Remove markdown code blocks
    let cleaned = output;

    // Remove ```json at the start
    cleaned = cleaned.replace(/^```json\s*/i, '');

    // Remove ``` at the end
    cleaned = cleaned.replace(/\s*```$/, '');

    // Trim whitespace
    cleaned = cleaned.trim();

    // Try parsing the cleaned output
    const parsed = JSON.parse(cleaned);

    return parsed;

  } catch (error) {
    // If all parsing fails, return error info
    return {
      error: true,
      message: 'Failed to parse AI output',
      original_error: error.message,
      raw_output: output.substring(0, 200) + '...' // First 200 chars for debugging
    };
  }
}

// Parse the output
const parsedProfile = parseAIOutput(aiOutput);

// Return the parsed profile with metadata
return {
  json: {
    ...parsedProfile,
    parsing_metadata: {
      parsed_at: new Date().toISOString(),
      original_output_length: aiOutput.length,
      parsing_successful: !parsedProfile.error
    }
  }
};

