// Enhanced Conversation State Manager with better state handling
const body = $input.item.json.body || $input.item.json;
const userMessage = body.message || '';
const incomingStep = body.step !== undefined ? body.step : 0;
const sessionData = body.sessionData || {};
const referenceImage = body.referenceImage || null;

const questions = [
  { id: 0, field: 'budget', question: 'What is your Budget per piece? (e.g., 1000/-)', type: 'number' },
  { id: 1, field: 'quantity', question: 'What is your quantity? (e.g., 100pcs)', type: 'number' },
  { id: 2, field: 'deadline', question: 'When do you need it? (e.g., 10th Oct)', type: 'text' },
  { id: 3, field: 'hasReference', question: 'Do you have any reference image? (yes/no)', type: 'boolean' },
  { id: 4, field: 'itemPreference', question: 'Do you have any item preference? (e.g., I need 4 dry fruits in the hamper plus diya and candle)', type: 'text' },
  { id: 5, field: 'packagingPreference', question: 'Do you have any Packaging Reference? (e.g., box type, eco-friendly)', type: 'text' }
];

let currentStep = incomingStep;

if (userMessage && currentStep < questions.length) {
  const questionToSave = questions[currentStep];
  sessionData[questionToSave.field] = userMessage;
  currentStep++;

  if (currentStep === 4 && referenceImage) {
    sessionData.referenceImageUrl = referenceImage;
  }
}

const currentQuestion = questions[currentStep];
let specialMessage = null;
let requiresImageParsing = false;
let showBoxOptions = false;

// After quantity is entered (now at step 2)
if (currentStep === 2 && sessionData.quantity) {
  const qty = parseInt(sessionData.quantity.toString().replace(/[^0-9]/g, ''));
  if (qty >= 200) {
    specialMessage = 'ðŸŽ‰ Great news! We have a special offer for 200+ pieces with 10% additional discount (40% off MRP total)!';
  } else if (qty > 0 && qty < 200) {
    specialMessage = 'ðŸ’¡ Tip: Orders above 200 pieces get 40% discount. Below 200 pieces get 35% discount.';
  }
}

// After reference image question (now at step 4)
if (currentStep === 4 && sessionData.hasReference) {
  const hasRef = sessionData.hasReference.toLowerCase();
  if (hasRef === 'yes' || hasRef === 'y') {
    requiresImageParsing = true;
    specialMessage = 'ðŸ“¸ Great! Please upload your reference image. AI will parse the data and create a similar product for you.';
  }
}

// After packaging preference question (now at step 6)
if (currentStep === 6 && sessionData.packagingPreference) {
  showBoxOptions = true;
  specialMessage = 'ðŸ“¦ Let me show you some packaging options that match your requirements...';
}

return {
  json: {
    currentStep: currentStep,
    userMessage: userMessage,
    sessionData: sessionData,
    currentQuestion: currentQuestion,
    specialMessage: specialMessage,
    requiresImageParsing: requiresImageParsing,
    showBoxOptions: showBoxOptions,
    isComplete: currentStep >= questions.length,
    sessionId: body.sessionId || $execution.id,
    timestamp: new Date().toISOString(),
    debug: {
      receivedStep: incomingStep,
      receivedMessage: userMessage,
      savedData: Object.keys(sessionData)
    }
  }
};

// Parse AI recommendations and extract structured data
const aiResponse = $('RAG: Smart Product + Packaging Selection').first().json;
const sessionData = $('Conversation Manager').item.json.sessionData;

// Safely extract response text and ensure it's a string
let responseText = '';
if (typeof aiResponse.text === 'string') {
  responseText = aiResponse.text;
} else if (typeof aiResponse.response === 'string') {
  responseText = aiResponse.response;
} else if (aiResponse.output && typeof aiResponse.output === 'string') {
  responseText = aiResponse.output;
} else if (typeof aiResponse === 'string') {
  responseText = aiResponse;
} else {
  // If it's an object, try to stringify it
  responseText = JSON.stringify(aiResponse);
}

// Extract recommended box
const boxMatch = responseText.match(/Box:\s*([^\n]+)/i);
const sizeMatch = responseText.match(/Size:\s*([0-9.]+)\s*[xÃ—]\s*([0-9.]+)\s*[xÃ—]\s*([0-9.]+)/i);
const packagingCostMatch = responseText.match(/Packaging:\s*Rs\s*([0-9,]+)/i);

const recommendedBox = {
  name: boxMatch ? boxMatch[1].trim() : 'Medium Box',
  length: sizeMatch ? parseFloat(sizeMatch[1]) : 30,
  width: sizeMatch ? parseFloat(sizeMatch[2]) : 20,
  height: sizeMatch ? parseFloat(sizeMatch[3]) : 15,
  cost: packagingCostMatch ? parseInt(packagingCostMatch[1].replace(/,/g, '')) : 300
};

recommendedBox.dimensions = `${recommendedBox.length}x${recommendedBox.width}x${recommendedBox.height}`;

return {
  json: {
    aiFullResponse: responseText,
    recommendedBox: recommendedBox,
    sessionData: sessionData,
    retrievedProducts: aiResponse.sourceDocuments?.length || 0
  }
};

// Generate final quotation
const sessionData = $('Conversation Manager').item.json.sessionData;
const aiData = $('Parse AI Recommendations').item.json;

const budget = parseInt(sessionData.budget) || 1000;
const quantity = parseInt(sessionData.quantity.replace(/[^0-9]/g, '')) || 100;

// Calculate discounts
const discountPercent = quantity >= 200 ? 40 : 35;
const bulkOrderDiscount = quantity >= 200 ? 10 : 0; // Additional bulk discount on total

const packagingCost = aiData.recommendedBox.cost;
const itemsCost = budget - packagingCost;

const subtotal = budget * quantity;
const additionalDiscount = (subtotal * bulkOrderDiscount) / 100;
const finalTotal = subtotal - additionalDiscount;

const quotation = {
  quotationId: `QT-${Date.now()}`,
  timestamp: new Date().toISOString(),
  customerDetails: {
    budget: budget,
    quantity: quantity,
    deadline: sessionData.deadline,
    itemPreferences: sessionData.itemPreference,
    packagingPreference: sessionData.packagingPreference
  },
  packagingDetails: {
    boxName: aiData.recommendedBox.name,
    boxSize: aiData.recommendedBox.dimensions,
    boxLength: aiData.recommendedBox.length,
    boxWidth: aiData.recommendedBox.width,
    boxHeight: aiData.recommendedBox.height,
    packagingCost: packagingCost,
    calculation: aiData.aiFullResponse
  },
  pricing: {
    budgetPerPiece: budget,
    itemsCostPerPiece: itemsCost,
    packagingCostPerPiece: packagingCost,
    quantity: quantity,
    subtotal: subtotal,
    mrpDiscountPercent: discountPercent,
    additionalBulkDiscount: bulkOrderDiscount,
    additionalDiscountAmount: additionalDiscount,
    finalTotal: finalTotal,
    savingsFromMRP: `You save ${discountPercent}% on MRP prices!`
  },
  aiRecommendation: aiData.aiFullResponse,
  specialOffer: bulkOrderDiscount > 0 ? `ðŸŽ‰ ${bulkOrderDiscount}% additional bulk discount + ${discountPercent}% MRP discount!` : `âœ¨ ${discountPercent}% discount on MRP applied!`
};

return { json: quotation };

// Format final comprehensive quotation with all details
const quotation = $input.first().json;
const imageUrl = $('Generate Product Image').first()?.json?.data?.[0]?.url || null;
const sessionData = quotation.customerDetails;

// Parse items from preferences
const itemsList = sessionData.itemPreferences ? sessionData.itemPreferences.split(/[,+&]/).map(item => item.trim()).filter(item => item.length > 0) : [];

return {
  json: {
    success: true,
    conversationComplete: true,
    message: 'âœ¨ Your custom gift hamper quotation is ready!',

    quotationId: quotation.quotationId,
    timestamp: quotation.timestamp,

    // Box Customization Details
    boxDetails: {
      name: quotation.packagingDetails.boxName,
      type: 'Customized ' + quotation.packagingDetails.boxName,
      size: quotation.packagingDetails.boxSize,
      dimensions: {
        length: quotation.packagingDetails.boxLength + ' cm',
        width: quotation.packagingDetails.boxWidth + ' cm',
        height: quotation.packagingDetails.boxHeight + ' cm'
      },
      material: sessionData.packagingPreference || 'Premium quality',
      cost: 'Rs ' + quotation.packagingDetails.packagingCost + '/-',
      customization: 'Available (Logo printing, Custom colors, Branded ribbon)'
    },

    // AI Generated Product Visualization
    productImage: {
      url: imageUrl,
      description: 'AI-generated preview of your custom gift hamper',
      note: 'Final product may vary slightly. This is a conceptual visualization.'
    },

    // Complete Item List
    itemsList: {
      items: itemsList,
      count: itemsList.length,
      customerRequest: sessionData.itemPreferences,
      aiRecommendation: quotation.aiRecommendation
    },

    // Comprehensive Pricing Breakdown
    pricing: {
      budgetPerPiece: 'Rs ' + quotation.pricing.budgetPerPiece + '/-',
      breakdown: {
        itemsCost: 'Rs ' + quotation.pricing.itemsCostPerPiece + '/-',
        packagingCost: 'Rs ' + quotation.pricing.packagingCostPerPiece + '/-'
      },
      quantity: quotation.pricing.quantity + ' pieces',
      subtotal: 'Rs ' + quotation.pricing.subtotal + '/-',

      // Discount Details
      discounts: {
        mrpDiscount: quotation.pricing.mrpDiscountPercent + '% off MRP',
        bulkDiscount: quotation.pricing.additionalBulkDiscount > 0 ? quotation.pricing.additionalBulkDiscount + '% additional bulk discount' : 'Not applicable (< 200 pieces)',
        totalDiscountAmount: 'Rs ' + quotation.pricing.additionalDiscountAmount + '/-',
        savingsMessage: quotation.pricing.savingsFromMRP
      },

      finalTotal: 'Rs ' + quotation.pricing.finalTotal + '/-',
      pricePerPiece: 'Rs ' + Math.round(quotation.pricing.finalTotal / quotation.pricing.quantity) + '/-'
    },

    // Special Offers
    specialOffer: quotation.specialOffer,

    // Delivery Details
    deliveryDetails: {
      deadline: sessionData.deadline,
      estimatedProduction: '7-10 business days',
      shipping: 'Free shipping on orders above Rs 50,000'
    },

    // AI Insights
    aiInsights: {
      packagingReason: 'AI selected this box based on item dimensions, quantity, and cushioning requirements',
      recommendation: quotation.aiRecommendation,
      productsRetrieved: quotation.packagingDetails.calculation
    },

    // Next Steps
    nextSteps: [
      '1. Review quotation and packaging details',
      '2. Request modifications if needed',
      '3. Confirm your order',
      '4. Proceed with payment (50% advance)',
      '5. Production and delivery by ' + sessionData.deadline
    ],

    // Contact & Support
    support: {
      phone: '+91 XXXXX XXXXX',
      email: 'orders@kiwihampers.com',
      whatsapp: 'Available for queries'
    }
  }
};

// Format ongoing conversation response
const convData = $('Conversation Manager').item.json;

// Safely check if Parse AI Recommendations executed
let aiData = null;
try {
  const aiNode = $('Parse AI Recommendations').first();
  if (aiNode && aiNode.json) {
    aiData = aiNode.json;
  }
} catch (error) {
  aiData = null;
}

let response = {
  success: true,
  conversationComplete: false,
  currentStep: convData.currentStep,
  sessionId: convData.sessionId,
  sessionData: convData.sessionData,
  debug: convData.debug
};

// Check if we have AI data (only at step 5)
if (aiData && aiData.aiFullResponse && convData.currentStep === 5) {
  response.message = aiData.aiFullResponse + '\n\n' + (convData.currentQuestion?.question || 'What would you like to do next?');
  response.aiRecommendations = aiData.aiFullResponse;
  response.packagingSuggestion = aiData.recommendedBox;
} else if (convData.specialMessage && convData.currentQuestion) {
  response.message = convData.specialMessage + '\n\n' + convData.currentQuestion.question;
} else if (convData.currentQuestion) {
  response.message = convData.currentQuestion.question;
} else {
  response.message = 'Processing your request...';
}

if (convData.currentStep === 3) {
  response.imageUploadEnabled = true;
}

if (convData.showBoxOptions) {
  response.showBoxOptions = true;
}

return { json: response };
